#include<iostream>#include<set>using namespace std;int main(){    #ifndef ONLINE_JUDGE    freopen("input.txt", "r", stdin);    freopen("output.txt", "w", stdout);    #endif    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);    int t;    cin>>t;    while(t--){    int n,e;    cin>>n>>e;    set<int>s;    for (int i = 0; i < n; ++i)    {      s.insert(i+1);    }    for (int i = 0; i < e; ++i)    {      int a,b,c;      cin>>a>>b>>c;      if(s.find(b)!=s.end())      s.erase(b);    }    int x=*s.begin();    for (int i = 0; i < n; ++i)    {      if(i+1!=x)        cout<<x<<" "<<i+1<<"\n";    }} return 0;}